refsect1.synopsis:
#!/usr/bin/env python
import sys, os, os.path import pygtk, gtk, gobject import pygst pygst.require("0.10") import gst
class GTK_Main:
def __init__(self): window = gtk.Window(gtk.WINDOW_TOPLEVEL) window.set_title("Mpeg-Player") window.set_default_size(500, 400) window.connect("destroy", gtk.main_quit, "WM destroy") vbox = gtk.VBox() window.add(vbox) hbox = gtk.HBox() vbox.pack_start(hbox, False) self.entry = gtk.Entry() hbox.add(self.entry) self.button = gtk.Button("Start") hbox.pack_start(self.button, False) self.button.connect("clicked", self.start_stop) self.movie_window = gtk.DrawingArea() vbox.add(self.movie_window) window.show_all()
self.player = gst.Pipeline("player") source = gst.element_factory_make("filesrc", "file-source") self.player.add(source) demuxer = gst.element_factory_make("mpegdemux", "demuxer") self.player.add(demuxer) demuxer.connect("pad-added", self.demuxer_callback) self.video_decoder = gst.element_factory_make("mpeg2dec", "video-decoder") self.player.add(self.video_decoder) self.audio_decoder = gst.element_factory_make("mad", "audio-decoder") self.player.add(self.audio_decoder) audioconv = gst.element_factory_make("audioconvert", "converter") self.player.add(audioconv) audiosink = gst.element_factory_make("autoaudiosink", "audio-output") self.player.add(audiosink) videosink = gst.element_factory_make("autovideosink", "video-output") self.player.add(videosink) self.queue = gst.element_factory_make("queue", "queue") self.player.add(self.queue) gst.element_link_many(source, demuxer) gst.element_link_many(self.video_decoder, videosink) gst.element_link_many(self.audio_decoder, audioconv, audiosink) bus = self.player.get_bus() bus.add_signal_watch() bus.enable_sync_message_emission() bus.connect('message', self.on_message) bus.connect('sync-message::element', self.on_sync_message)
def start_stop(self, w): if self.button.get_label() == "Start": filepath = self.entry.get_text() if os.path.exists(filepath): self.button.set_label("Stop") self.player.get_by_name("file-source").set_property('location', filepath) self.player.set_state(gst.STATE_PLAYING) else: self.player.set_state(gst.STATE_NULL) self.button.set_label("Start")
def on_message(self, bus, message): t = message.type if t == gst.MESSAGE_EOS: self.player.set_state(gst.STATE_NULL) self.button.set_label("Start") elif t == gst.MESSAGE_ERROR: err, debug = message.parse_error() print "Error: %s" % err, debug self.player.set_state(gst.STATE_NULL) self.button.set_label("Start")
def on_sync_message(self, bus, message): if message.structure is None: return message_name = message.structure.get_name() if message_name == 'prepare-xwindow-id': imagesink = message.src imagesink.set_property('force-aspect-ratio', True) imagesink.set_xwindow_id(self.movie_window.window.xid)
def demuxer_callback(self, demuxer, pad): if pad.get_property("template").name_template == "video_%02d": dec_pad = self.video_decoder.get_pad("sink") pad.link(dec_pad) elif pad.get_property("template").name_template == "audio_%02d": dec_pad = self.audio_decoder.get_pad("sink") pad.link(dec_pad)
GTK_Main() gtk.gdk.threads_init() gtk.main()
